import React, {useState} from 'react';
import './App.css';

// let bpm = useState(100);
// let play = useState(false)

const playDrums = () => {
  let snare = "https://freewavesamples.com/files/Ensoniq-ESQ-1-Snare.wav"

  snare.audio.play()
  snare.audio.loop = true
}

function App() {
  return (
    <div className="container">
      <header>
        <button id="play-button" on>
          Play/Pause
        </button>
      </header>
      <div className="container-player">
        <button className="snare">
          Snare
        </button>
      </div>
    </div>
  );
}

export default App;
